mapper.select:
SELECT D.RNUM, D.GY_NUM, D.GY_NAME, D.GY_ADDR1, D.GY_ADDR2, D.GY_PHONE, D.GY_CNAME FROM ( SELECT ROWNUM AS RNUM, C.GY_NUM, C.GY_NAME, C.GY_ADDR1, C.GY_ADDR2, C.GY_PHONE, C.GY_CNAME FROM ( SELECT GY_NUM, GY_NAME, GY_ADDR1, GY_ADDR2, GY_PHONE, GY_CNAME FROM GYM
WHERE GY_NAME LIKE '%' || #{SEARCH_WORD} || '%'
WHERE GY_CNAME = #{SEARCH_WORD}
WHERE GY_ADDR1 LIKE '%' || #{SEARCH_WORD} || '%'
ORDER BY GY_NAME ASC ) C WHERE ROWNUM <= #{END} ) D WHERE D.RNUM >= #{START}
