mapper.select:
SELECT D.RNUM, D.RV_NUM, D.RV_TITLE, D.RV_DATE, D.RV_HIT, D.MB_ID FROM ( SELECT ROWNUM AS RNUM, C.RV_NUM, C.RV_TITLE, C.RV_DATE, C.RV_HIT, C.MB_ID FROM ( SELECT A.RV_NUM AS RV_NUM, A.RV_TITLE AS RV_TITLE, TO_CHAR(A.RV_DATE,'YYYY.MM.DD') AS RV_DATE, A.RV_HIT AS RV_HIT, B.MB_ID AS MB_ID FROM REVIEW A, MEMBER B WHERE A.MB_CODE = B.MB_CODE
AND RV_TITLE LIKE '%' || #{SEARCH_WORD} || '%'
AND RV_NUM = #{SEARCH_WORD}
AND RV_BODY LIKE '%' || #{SEARCH_WORD} || '%'
ORDER BY A.RV_NUM DESC ) C WHERE ROWNUM <= #{END} ) D WHERE D.RNUM >= #{START}
